mapper.select:
SELECT
    COUNT(*)
FROM
    cart_info
WHERE

        cart_ckid = #{cart_ckid}


        user_id = #{user_id}

AND
    goods_no = #{goods_no}

mapper.insert:
INSERT INTO cart_info(

         cart_ckid


         user_id

        ,goods_no

        ,option_no

        ,goods_quantity
        ,cart_cklimit
)
VALUES(

         #{cart_ckid}


         #{user_id}

        ,#{goods_no}

        ,#{option_no}

        ,#{goods_quantity}
        ,CURRENT_TIMESTAMP
)

mapper.select:
SELECT c.cart_no ,c.user_id ,c.goods_no ,c.option_no ,c.goods_quantity ,c.select_yn ,c.cart_cklimit ,c.cart_ckid ,g.goods_title ,g.goods_desc ,g.goods_price ,g.frequency_yn FROM cart_info c, goods_info g WHERE c.goods_no = g.goods_no AND
cart_ckid = #{cart_ckid}
user_id = #{user_id}

mapper.update:
UPDATE
    cart_info
SET
    user_id = #{user_id}
WHERE
    cart_ckid = #{cart_ckid}

UPDATE
    cart_info
SET
    goods_quantity = #{goods_quantity}
WHERE
    cart_no = #{cart_no};
